import React from 'react';
import {useRef, useState, useEffect} from 'react';
import "./journal.css";

const Journal = () => {
    const [id, setId] = useState(Date.now())
    const dateRef = useRef(null);
    const thoughtRef = useRef(null);
    const [myThought, setMyThought] = useState([]);
    

    const handleSubmit = (e) => {
        e.preventDefault();
        const journalObject = {
          id: id,
          date: dateRef.current.value,
          thought: thoughtRef.current.value,
          }
          setMyThought([...myThought, journalObject]);
    
          dateRef.current.value= null;
          thoughtRef.current.value= null;
          setId(Date.now());
         
      }
      useEffect(() => {
        localStorage.setItem('MyDay', JSON.stringify(myThought))
      }, [myThought])
    
  return (
    <div className='container'>
        <h1 className='title'>My Coding Journal</h1>
        <form action="" onSubmit={handleSubmit}>
            <label className='firstLabel'>Thoughts for the Day</label>
            <input className='date' type="date" ref={dateRef}/><br/>
            <textarea className='message' id="" cols="36" rows="5" ref={thoughtRef}></textarea><br/>
            <input type="submit"value='Save' />
            <input type="reset" value='Clear'/>
        </form>
        <div>
            <h4>Thoughts for the Day</h4>
            {myThought.map((info)=>(
            <h4 className='messageContainer'>{info.date}: {info.thought}</h4>
        ))}
        </div>
    </div>
  )
}

export default Journal
