import React from 'react';
import {useRef, useState, useEffect} from 'react';
import JournalList from './component/JournalList';

const Journal = () => {
    const dateRef = useRef(null);
    const thoughtRef = useRef(null);
    const [contactList, setContactList] = useState([]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const myThoughtObject = {
    
        
          date: dateRef.current.value,
          thought: thoughtRef.current.value,
          }
          setContactList([...contactList, myThoughtObject]);
    
          dateRef.current.value= null;
          thoughtRef.current.value= null;
         
      }
      useEffect(() => {
        localStorage.setItem('ContactUs', JSON.stringify(myThoughtObject))
      }, [contactList])
    
  return (
    <div>
        <form action="" onSubmit={handleSubmit}>
            <label>Thoughts for the Day</label>
            <input type="date" /><br/>
            <textarea name="" id="" cols="36" rows="10"></textarea><br/>
            <input type="submit"value='Save' />
        </form>
    </div>
  )
}

export default Journal
